import { useQueries, useQuery } from "react-query";
import { useParams } from "react-router";
import { fetchCoinHistory } from "./api";
import ApexChart from "react-apexcharts";
interface IHistorical {
    time_open: string;
time_close: string;
high:number;
open:number; 
low: number;
close: number;
volume:number;
market_cap:number;
}

interface CharProps{
    coinId:string;
}
function Chart({coinId}:CharProps){
    const {isLoading,data} = useQuery<IHistorical[]>(
    ["ohlcv",coinId],
     () => fetchCoinHistory(coinId),
     {
         refetchInterval: 10000,
     }
     );

    return(<div>{isLoading ? "Loading char...":<ApexChart type="candlestick" 
    series={[
       
        {
        name:"price",
        data:[data?.map(price=>price.open),data?.map(price=>price.high),data?.map(price=>price.low),data?.map(price=>price.close)],
        },
    ]}
   
   />}</div>);
   
}
export default Chart;
