const BASE_URL=`https://api.coinpaprika.com/v1/`;

export function fetchCoins(){
    return fetch(`${BASE_URL}/coin`).then((response)=>
    response.json());

}
export function fetchCoinInfo(coinId:string){
    return fetch(`${BASE_URL}/coin/${coinId}`).then((response)=>
    response.json());


}
